import React from 'react';
import {Link} from 'react-router-dom';

class MainSearch extends React.Component{

    constructor(props){
        super(props);

        //initial state
        this.state = {
            searchTerm: ""
        };

        //bind the 'this' keyword to this function
        this.SearchItem = this.SearchItem.bind(this);    
    }


    //this item grabs the text from search bar
    SearchItem(e){
        e.preventDefault();

        const searchQuery = document.getElementById("searchbar");

        //if the search bar is not empty save 
        if (searchQuery.value !== ""){
            this.setState({
                searchTerm: searchQuery
            });
        }

        console.log(searchQuery);
    }


    render(){
        return(
        
        <div className="container">


            <div className="columns">
                <div className="column is-one-fifth"></div>
                <div className="column">

                    <div className="field">
                        <div class="control">
                            <input id="searchbar" class="input" type="text" onChange={this.SearchItem} placeholder="Search for cards..."></input>
                        </div>
                    </div>

                    <div className="navbar-start">
                        <a href="/cardDisplay" className="button is-small is-primary is-outlined is-rounded">
                            search  
                        </a>
                    </div>

                </div>
                <div className="column is-one-fifth"></div>
            </div>
        </div>
        );
    }
}

export default MainSearch

