//import react
import React from 'react';

//import built components to render within the app
import MainSearch from './MainSearch';
import CardDisplay from './CardDisplay';
import Nav from './Nav';
//import css framework 'Bulma'
import 'bulma/css/bulma.css'

//add react component to allow the switching between pages within the app
import {BrowserRouter as Router, Switch, Route} from 'react-router-dom';


class App extends React.Component{

  constructor(props){
    super(props);

    //initial state
    this.state = {
        cardName: ""
    };
  } 

  setCard(card){
    return () => {
      this.setState({
        cardName: card
      });
    }
  }

  //render function from react, written in JSX
  //the router component lists the components in the directory so that the can referenced by other components
  render() 
  {
    return (

      <Router> 
      <div className ="App">
        <Nav />
        <section className="section">
          <Switch>
            <p>Current Card: {this.state.cardName}</p>
            <Route path="/" exact component ={MainSearch} data={this.setCard.bind(this)}/>
            <Route path="/cardDisplay" exact component ={CardDisplay}/> 
          </Switch> 
        </section>
      </div>
    </Router>  

    );
  }
}     

export default App;
